mapper.insert:
 INSERT INTO tbl_freeboard(  bno 
 					   , category
					       , title 
					       , content 
					       , writer  )
               VALUES(    #{bno}
                        , #{category}
              		   , #{title}
              		   , #{content}
              		   , #{writer}  )

mapper.select:
SELECT	bno, category, title, content, writer, date
 FROM tbl_freeboard
 WHERE bno = #{bno}

mapper.update:
UPDATE tbl_freeboard
   SET category    =   #{category},
       title    =   #{title},
   	   content  =   #{content}
 WHERE bno = #{bno}

UPDATE tbl_freeboard SET
HIT = HIT+1
WHERE BNO = #{bno}
